<?php\ninclude 'header.php';\n\nif (!is_object($db)) {\n    die("Database connection failed.");\n}\n\n$stmt = $db->prepare("SELECT * FROM users WHERE id = ?");\n$stmt->bind_param("i", $_GET['id']);\n$stmt->execute();\n$user_res = $stmt->get_result();\n$user = $user_res->fetch_assoc();\n\nif ($user) {\n    echo "<h1>" . htmlspecialchars($user['username']) . "'s Profile</h1>";\n} else {\n    echo "User not found.";\n}\n\ninclude 'footer.php';\n?>