$url = urldecode($object_list_url);\n$ch = curl_init();\n\n// Set the URL that you want to fetch.\ncurl_setopt($ch, CURLOPT_URL, $url);\n\n// Return the response as a string instead of outputting directly.\ncurl_setopt($ch, CURLOPT_RETURNTRANSFER, 1);\n\n// Execute the request and store the result in $xmlstr.\n$xmlstr = curl_exec($ch);\n\n// Check for cURL errors.\nif (curl_errno($ch)) {\n    echo 'Error:' . curl_error($ch);\n} else {\n    // Close cURL session handle.\n    curl_close($ch);\n\n    // Process your XML as before.\n    $obj = new SimpleXMLElement($xmlstr, LIBXML_NOCDATA);\n}