<?php\n// Database connection setup\n$host = 'localhost';\n$dbname = 'your_db_name';\n$username = 'your_username';\n$password = 'your_password';\n\ntry {\n    $pdo = new PDO("mysql:host=$host;dbname=$dbname", $username, $password);\n    $pdo->setAttribute(PDO::ATTR_ERRMODE, PDO::ERRMODE_EXCEPTION);\n\n    // Fetch data from the database\n    $stmt = $pdo->query("\n        SELECT o.org_name, e.emp_name, e.phone, e.address\n        FROM organizations o\n        JOIN employees e ON o.id = e.org_id\n        ORDER BY o.org_name, e.emp_name\n    ");\n\n    $currentOrgName = null;\n    echo "<table border='1'>";\n    while ($row = $stmt->fetch(PDO::FETCH_ASSOC)) {\n        if ($row['org_name'] !== $currentOrgName) {\n            // If it's a new organization, close the previous one and start a new row\n            if ($currentOrgName !== null) {\n                echo "</td></tr>";\n            }\n            // Start a new row for the new organization with rowspan\n            $orgRowsCount = countOrganizations($stmt->fetchAll(), $row['org_name']);\n            $stmt->execute(); // Re-execute to reset pointer after fetchAll\n            echo "<tr><td rowspan='$orgRowsCount'>" . htmlspecialchars($row['org_name']) . "</td>";\n            $currentOrgName = $row['org_name'];\n        }\n        // Output employee details\n        echo "<td>" . htmlspecialchars($row['emp_name']) . ", " . htmlspecialchars($row['phone']) . ", " . htmlspecialchars($row['address']) . "</td></tr><tr>";\n    }\n    if ($currentOrgName !== null) {\n        echo "</td></tr>"; // Close the last open row\n    }\n    echo "</table>";\n} catch (PDOException $e) {\n    die("Error: " . $e->getMessage());\n}\n\n// Function to count number of employees for a specific organization in remaining rows\nfunction countOrganizations($rows, $orgName) {\n    $count = 0;\n    foreach ($rows as $row) {\n        if ($row['org_name'] === $orgName) {\n            $count++;\n        }\n    }\n    return $count;\n}\n?>