// Create a new SimpleXMLElement as the root of your XML document.\n$xml = new SimpleXMLElement("<?xml version=\"1.0\"?><root></root>");\n\n// Add elements to the XML.\n$xml->addChild('child', 'This is a child node');\n$xml->addChild('anotherChild', 'This is another child node');\n\n// Adding attributes\n$child = $xml->addChild('item');\n$child->addAttribute('type', 'example');\n$child->addChild('description', 'An example item');\n\n// Output the XML as a string.\necho $xml->asXML();