// Register a function used to autoload classes\nspl_autoload_register(function($class_name) {\n    // Check if the class name starts with 'vPrimaryKey_'\n    if (strpos($class_name, 'vPrimaryKey_') === 0) {\n        // Extract the table name from the class name\n        $tableName = substr($class_name, strlen('vPrimaryKey_'));\n\n        // Define the class dynamically\n        eval("\n            class {$class_name} extends vPrimaryKey {\n                protected \$table;\n\n                public function __construct() {\n                    \$this->table = '{$tableName}';\n                }\n\n                public function getTable() {\n                    return \$this->table;\n                }\n            }\n        ");\n    }\n});\n\n// Example usage\n$pkClass = 'vPrimaryKey_Products';\n$instance = new $pkClass();\necho $instance->getTable(); // Outputs: Products