<?php\n// URL of the external XML file\n$xmlUrl = "http://example.com/yourfile.xml";\n\n// Load the XML file\n$xml = simplexml_load_file($xmlUrl);\n\nif ($xml === false) {\n    echo "Failed loading XML";\n} else {\n    // Iterate through each 'child' element in the 'parent'\n    foreach ($xml->child as $child) {\n        // Read the value of 'grandchild'\n        $grandChildText = $child->grandchild;\n        \n        // Display the grandchild text\n        echo "Grandchild Text: " . htmlspecialchars($grandChildText) . "<br>";\n    }\n}\n?>