<?php\n$ciphertext = $_GET['ciphertext'];\n$iv = $_GET['iv'];\n$key = '0123456789abcdef'; // Ensure this matches the key length (e.g., 16 bytes for AES-128)\n\n// Decode Base64 encoded data\n$cipherData = base64_decode($ciphertext);\n$decodedIV = base64_decode($iv);\n\n// Decryption using OpenSSL in PHP\n$plainText = openssl_decrypt($cipherData, 'AES-128-CBC', $key, OPENSSL_RAW_DATA, $decodedIV);\n\nif ($plainText === false) {\n    echo "Decryption failed";\n} else {\n    echo "Decrypted message: " . $plainText;\n}\n?>