$host = "myip";\n   $user = "root";\n   $pass = "";\n   $db = "mydatabase";\n\n   // Create connection\n   $conn = mysqli_connect($host, $user, $pass, $db);\n\n   // Check connection\n   if (!$conn) {\n       die("Connection failed: " . mysqli_connect_error());\n   }\n\n   $result = mysqli_query($conn, "SELECT COUNT(online) FROM characters WHERE online=1");\n   $on = mysqli_fetch_row($result);\n   echo "<br>Online: " . $on[0] . "<br><br>";\n\n   // Close connection\n   mysqli_close($conn);