// Initialize cURL session\n$ch = curl_init();\n\n// Set the URL for the request\ncurl_setopt($ch, CURLOPT_URL, "https://nexus.passport.com/rdr/pprdr.asp");\n\n// Return the transfer as a string\ncurl_setopt($ch, CURLOPT_RETURNTRANSFER, 1);\n\n// Set user agent to mimic a specific browser if necessary\ncurl_setopt($ch, CURLOPT_USERAGENT, 'MSNphp/1.0 (PHP; U; PHP 5; en-US)');\n\n// Execute cURL request and fetch the response\n$response = curl_exec($ch);\n\n// Check for errors and handle them appropriately\nif(curl_errno($ch)) {\n    echo 'cURL error: ' . curl_error($ch);\n} else {\n    echo $response;\n}\n\n// Close cURL session\ncurl_close($ch);