<?php\n// Set timezone if needed (matches your server/default or DB GMT)\ndate_default_timezone_set('UTC');\n\n$last_night_at_midnight = strtotime('midnight yesterday');\n$tonight_at_midnight = strtotime('midnight today');\n\necho "Last Night Midnight: " . date('Y-m-d H:i:s', $last_night_at_midnight) . "\n";\necho "Tonight Midnight: " . date('Y-m-d H:i:s', $tonight_at_midnight) . "\n";\n\n// Use these variables in your SQL query\n$query = "SELECT * WHERE start > $last_night_at_midnight AND start < $tonight_at_midnight";\n?>