<?php\n\n$api_key = 'your_api_key_here';\n$url = 'https://your-chevereto-domain.com/api/1/upload';\n\n$postfields = array(\n    'source' => new CURLFile(realpath('localfile.jpg')),\n    'key' => $api_key,\n);\n\n$ch = curl_init();\ncurl_setopt($ch, CURLOPT_URL, $url);\ncurl_setopt($ch, CURLOPT_POST, 1);\ncurl_setopt($ch, CURLOPT_POSTFIELDS, $postfields);\ncurl_setopt($ch, CURLOPT_RETURNTRANSFER, true);\n\n$response = curl_exec($ch);\n$httpCode = curl_getinfo($ch, CURLINFO_HTTP_CODE);\ncurl_close ($ch);\n\nif ($httpCode == 200) {\n    $jsonResponse = json_decode($response, true);\n    if (isset($jsonResponse['image']['url'])) {\n        echo "Image uploaded successfully. Shareable URL: " . $jsonResponse['image']['url'];\n    } else {\n        echo "Failed to get shareable URL from response.";\n    }\n} else {\n    echo "Upload failed with HTTP code: " . $httpCode;\n}\n\n?>