<?php\n\nclass Page extends CI_Controller {\n    public function show($template = 'default_template') {\n        $this->load->config('templates');\n        $template_vars = $this->config->item('template_vars');\n\n        if (isset($template_vars[$template])) {\n            // Fetch data from database based on template variables\n            $data['variables'] = [];\n            foreach ($template_vars[$template] as $var) {\n                // Simulate fetching content from DB\n                $data['variables'][$var] = 'Content for ' . strtoupper($var);\n            }\n\n            $this->load->view('templates/' . $template, $data);\n        } else {\n            show_404();\n        }\n    }\n}