<?php\n\n$query = mysql_query("SELECT threads.id AS thread_id, users.id AS user_id, ...\n                      FROM threads\n                      INNER JOIN users\n                      ON threads.poster = users.id\n                      WHERE threads.forum_id = 11");\n\nwhile($row = mysql_fetch_array($query)) {\n    echo '<a href="thread.php?id=' . htmlspecialchars($row['thread_id']) . '">Link</a>';\n}\n\n?>