<select name="country" style="width:180px;" onChange="do_get_rest_popup(this.value)">\n    <?php\n    // Define an associative array of countries with ID as key and country name as value\n    $countries = [\n        217 => 'Turkmenistan',\n        218 => 'Turks and Caicos Islands',\n        219 => 'Tuvalu',\n        220 => 'Uganda',\n        221 => 'Ukraine',\n        222 => 'United Arab Emirates',\n        223 => 'United Kingdom (Great Britain)',\n        224 => 'United States',\n        // Add more countries here as needed\n    ];\n\n    // Assume $line_member['country'] contains the ID of the user's country\n    $userCountryId = isset($line_member['country']) ? intVal($line_member['country']) : null;\n\n    // Loop through the countries array to create dropdown options\n    foreach ($countries as $id => $name) {\n        ?>\n        <option value="<?php echo htmlspecialchars($id, ENT_QUOTES); ?>" <?php if ($userCountryId == $id) { echo 'SELECTED'; } ?>>\n            <?php echo htmlspecialchars($name, ENT_QUOTES); ?>\n        </option>\n        <?php\n    }\n    ?>\n</select>