function getNextMonths($numberOfMonths) {\n    $months = [];\n    $date = new DateTime('first day of this month');\n\n    for ($i = 0; $i < $numberOfMonths; $i++) {\n        $months[] = $date->format('F');\n        $date->add(new DateInterval('P1M'));\n    }\n\n    return $months;\n}\n\n$months = getNextMonths(12);\n$currentMonth = date('F');\n\necho '<a href="http://mydomain.com/' . strtolower($currentMonth) . '/" title="' . ucfirst($currentMonth) . '">' . $currentMonth . '</a><br />';\n\nforeach ($months as $i => $month) {\n    if ($month === $currentMonth && $i === 0) continue; // Skip the current month since it's already shown\n    echo '<a href="http://mydomain.com/' . strtolower($month) . '/" title="' . ucfirst($month) . '">' . $month . '</a><br />';\n}