<?php\n\nuse Drupal\node\Entity\Node;\n\n// Sample function to import nodes from XML (already parsed) data.\nfunction import_nodes_from_xml($xml_data) {\n  // Loop through each item in the XML parsed data\n  foreach ($xml_data as $item) {\n    // Create a new node object of your required content type\n    $node = Node::create([\n      'type'        => 'article', // Change this to your custom content type machine name\n      'title'       => $item['title'], // Replace with the actual field names from $item\n      'body'        => [\n        'value'     => $item['content'], // Replace 'content' with the actual key in your XML data\n        'format'    => 'full_html',\n      ],\n      // Add any other fields you need to map here...\n      'uid'         => 1, // Set the user ID of the node creator (replace as needed)\n    ]);\n\n    // Save the node to the database.\n    $node->save();\n    \n    // Optional: If you're using pathauto and want to regenerate paths for these nodes.\n    \Drupal::service('pathauto.generator')->updateEntityAliases($node, 'insert');\n\n    drupal_set_message(t("Created node from XML data with title %title", ['%title' => $item['title']]));\n  }\n}\n\n// Example usage:\n$xml_data = array(\n  // Your parsed XML data here\n);\nimport_nodes_from_xml($xml_data);\n\n?>