public function autosuggest() {\n       $this->autoRender = false;\n       if ($this->RequestHandler->isAjax()) {\n           $query = $this->request->query['term'];\n           $results = $this->YourModel->find('all', array(\n               'conditions' => array('YourModel.name LIKE' => '%' . $query . '%'),\n               'fields' => array('YourModel.name'),\n               'limit' => 10\n           ));\n           $names = Set::extract('/YourModel/name', $results);\n           echo json_encode($names);\n       }\n   }