function set(&$x, &$y)\n{\n    // Directly assigning $y[2] to $x by reference\n    $x = &$y[2];\n}\n\n$y = array(\n    0,\n    1,\n    array('something')\n);\n\n$x = array('old_val');\nprint "1. inited, x is:\n";\nprint_r($x);\n\nset($x, $y);\n\nprint "\n2. now, x is: ";\nprint_r($x);\n\n// Modifying $x should affect $y[2]\n$x[] = 'new_value';\nprint "\n3. modified x, y is: ";\nprint_r($y);