<?php\n$imageUrls = [\n    "http://domain.com/pic1.jpg",\n    "http://domain.com/pic2.jpg",\n    // Add more URLs as needed\n];\n\n$destinationFolder = '/path/to/your/server/folder/';\n\nforeach ($imageUrls as $url) {\n    $imageName = basename($url);\n    $imageData = file_get_contents($url);\n\n    if ($imageData === FALSE) {\n        echo "Error fetching the image from URL: $url\n";\n    } else {\n        $destinationPath = $destinationFolder . $imageName;\n        if (file_put_contents($destinationPath, $imageData)) {\n            echo "Image successfully saved to: $destinationPath\n";\n        } else {\n            echo "Error saving the image to: $destinationPath\n";\n        }\n    }\n}\n?>