<?php\nrequire 'includes/connect.php'; // database connection\n\n$country = intval($_REQUEST['country']); // get from form (index.php)\n$q = "SELECT city FROM city WHERE countryid=" . $country;\n$result = $mysqli->query($q) or die(mysqli_error($mysqli));\n\nif ($result && $result->num_rows > 0) {\n?>\n<select name="city">\n<option>Select City</option>\n<?php\n    while ($row = $result->fetch_object()) { \n        $src = htmlspecialchars($row->city); // use htmlspecialchars to prevent XSS\n?>\n<option value="<?php echo $id; ?>"><?php echo $src; ?></option>\n<?php\n        $id = $id + 1;\n    }\n?>\n</select>\n<?php\n} else {\n?>\n<select name="city">\n<option>Select City</option>\n</select>\n<?php\n}\n?>