<?php\n\n$name = trim($_POST['name']);\n$email = $_POST['email'];\n$comments = $_POST['comments'];\n\n$site_owners_email = 'zeckdude@gmail.com'; // Replace this with your own email address\n$site_owners_name = 'Chris Seckler'; // replace with your name\n\n$error = [];\n\nif (strlen($name) < 2) {\n    $error['name'] = "Please enter your name";\n}\n\nif (!filter_var($email, FILTER_VALIDATE_EMAIL)) {\n    $error['email'] = "Please enter a valid email address";\n}\n\nif (strlen($comments) < 3) {\n    $error['comments'] = "Please leave a comment.";\n}\n\nif (!$error) {\n\n    require_once('phpMailer/class.phpmailer.php');\n    $mail = new PHPMailer();\n\n    $mail->From = $email;\n    $mail->FromName = $name;\n    $mail->Subject = "Website Contact Form";\n    $mail->AddAddress($site_owners_email, $site_owners_name);\n    $mail->Body = $comments;\n\n    if ($mail->Send()) {\n        ?>\n        <script type="text/javascript">\n            $(function(){\n                $('#container').fadeOut(1000);\n            });\n        </script>\n        <?php\n        echo "<li class='success'> Congratulations, " . htmlspecialchars($name) . ". We've received your email. We'll be in touch as soon as we possibly can! </li>";\n        echo nl2br("<b>Message Sent:</b>\n\nFrom: $name\n\nEmail: $email\n\nMessage: $comments\n\n<a href='http://www.google.com'>Link</a>");\n    } else {\n        echo "<li class='error'>There was an error sending your message. Please try again later.</li>";\n    }\n\n} else {\n\n    foreach ($error as $msg) {\n        echo "<li class='error'>" . htmlspecialchars($msg) . "</li>";\n    }\n}\n\n?>