// Start the session without automatically regenerating the ID\nZend_Session::start(false);\n\n// Save current session data\n$namespace = new Zend_Session_Namespace();\n$data = $namespace->toArray();\n\n// Regenerate the session ID\nZend_Session::regenerateId(true, true); // The second parameter ensures old session is deleted\n\n// Restore saved session data\nforeach ($data as $key => $value) {\n    $namespace->$key = $value;\n}