function log_change($object_type, $object_id, $action, $changes = '') {\n    global $wpdb;\n    $user_id = get_current_user_id();\n    $wpdb->insert(\n        'wp_change_log',\n        array(\n            'object_type' => $object_type,\n            'object_id' => $object_id,\n            'action' => $action,\n            'user_id' => $user_id,\n            'changes' => serialize($changes),\n        ),\n        array('%s', '%d', '%s', '%d', '%s')\n    );\n}