public function indexAction()\n{\n    $request = $this->getRequest();\n    $params = $request->getQuery(); // for GET parameters\n    if ($params->count() == 0) {\n        $matchedRouteName = $this->event->getRouteMatch()->getMatchedRouteName();\n        $routeMatchParams = $this->event->getRouteMatch()->getParams();\n        // Assuming the unnamed parameter is 'id' and you have set it as route constraint\n        $id = $routeMatchParams['id'];\n    } else {\n        $id = $params->get('id');\n    }\n\n    if ($id === null) {\n        $id = 1; // or any default value you want to use\n    }\n\n    // Use $id\n}