class Database {\n    public function getQuery($queryName) {\n        // Your database query logic here\n        return "Executing query: $queryName";\n    }\n}\n\nclass Member {\n    public function lastRegistered() {\n        // Logic to find the last registered member\n        return "Last registered member details";\n    }\n}\n\nclass Chesterx {\n    protected $database;\n    protected $member;\n\n    public function __construct(Database $database, Member $member) {\n        $this->database = $database;\n        $this->member = $member;\n    }\n}\n\nclass Hello extends Chesterx {\n    public function ornekFunc() {\n        echo $this->database->getQuery('popularNews');\n        echo $this->member->lastRegistered();\n    }\n}\n\n// Usage\n$database = new Database();\n$member = new Member();\n$hello = new Hello($database, $member);\n$hello->ornekFunc();