function isAnimatedGif($filePath) {\n    $file = fopen($filePath, 'rb');\n    fseek($file, 0);\n    stream_get_contents($file, 13); // Skip the file header\n\n    $frames = 0;\n    while (!feof($file)) {\n        $chunkHeader = fread($file, 6); // Read the chunk header\n        if (strlen($chunkHeader) < 6) break;\n\n        $typeCode = substr($chunkHeader, 3, 3);\n        if ($typeCode == 'NET') { // Image Descriptor Block\n            stream_get_contents($file, 10); // Skip image descriptor\n            fread($file, 256); // First table is the global color map\n            while (true) {\n                $subBlock = ord(fread($file, 1)); // Read subblock length\n                if ($subBlock === 0) break;\n                stream_get_contents($file, $subBlock);\n            }\n        } elseif ($typeCode == 'GCE') { // Graphics Control Extension Block\n            fread($file, 6); // Read the rest of the block header and data\n            $frames++;\n            if ($frames > 1) {\n                fclose($file);\n                return true; // More than one frame detected\n            }\n        } elseif (ord($chunkHeader[0]) === 0x3B) { // Terminator\n            break;\n        } else {\n            $subBlock = ord(fread($file, 1)); // Read subblock length\n            fseek($file, -$subBlock - 2, SEEK_CUR); // Move back to the end of subblocks\n        }\n    }\n\n    fclose($file);\n    return false; // Less than or equal to one frame detected\n}\n\n// Usage:\n$filePath = 'your-image.gif';\nif (isAnimatedGif($filePath)) {\n    echo "The GIF is animated.";\n} else {\n    echo "The GIF is not animated.";\n}