<?php\n\n// Define command to run mysql shell\n$cmd = 'mysql -u root -pxxxx db_name';\n\n// Open a process with proc_open\n$descriptorspec = array(\n   0 => array("pipe", "r"),  // stdin is a pipe that the child will read from\n   1 => STDOUT,              // stdout is redirected to PHP's output\n   2 => STDERR               // stderr is redirected to PHP's output\n);\n\n// Start MySQL shell as a detached process\n$process = proc_open($cmd, $descriptorspec, $pipes);\n\nif (is_resource($process)) {\n    fclose($pipes[0]);  // Close the stdin pipe\n    proc_close($process); // Close the process resource\n    \n    // Exit PHP script immediately after starting MySQL shell\n    exit(0);\n}\n?>