<?php\n// Assume $s3Url is the authorized signed URL of the S3 object\n$s3Url = 'https://your-bucket.s3.amazonaws.com/unique-id-file.ext';\n$newFileName = 'desired-filename.ext';\n\n$ch = curl_init($s3Url);\ncurl_setopt($ch, CURLOPT_RETURNTRANSFER, false); // Return as stream\n\n// Set necessary headers for browser to handle the file correctly\nheader('Content-Type: application/octet-stream');\nheader('Content-Disposition: attachment; filename="' . $newFileName . '"');\n\n$responseCode = curl_getinfo($ch, CURLINFO_HTTP_CODE);\n\nif ($responseCode == 200) {\n    // Output stream directly to the browser\n    $out = fopen('php://output', 'w'); \n    stream_copy_to_stream(curl_exec($ch), $out);\n    fclose($out);\n} else {\n    echo "Error: Could not retrieve file. HTTP response code: $responseCode";\n}\n\ncurl_close($ch);\n?>