// Load the HTML Purifier library\n   require_once 'vendor/autoload.php';\n   \n   use HTMLPurifier;\n   use HTMLPurifier_Config;\n\n   // Configuration for HTMLPurifier to allow specific tags and attributes\n   $config = HTMLPurifier_Config::createDefault();\n   $config->set('HTML.Allowed', 'embed[src|type], object[data|type]'); // Adjust as necessary\n\n   // Create a new instance of HTML Purifier with the specified configuration\n   $purifier = new HTMLPurifier($config);\n\n   // Get user input (e.g., from an HTML form)\n   $dirtyHTML = $_POST['user_input']; // Assume this is where your <embed> or <object> code comes from\n\n   // Clean the HTML\n   $cleanHTML = $purifier->purify($dirtyHTML);\n\n   // Now you can safely store or display $cleanHTML\n   echo htmlspecialchars($cleanHTML, ENT_QUOTES, 'UTF-8');