$o = new Console_Getopt;\nlist($options, $arguments) = $o->getopt($argv, "a:b:c");\n\n// Transform options array into associative array\n$optionArray = [];\nforeach ($options as $keyval) {\n    list($key, $value) = $keyval;\n    // If the key already exists and is not null, store values in an array\n    if (isset($optionArray[$key]) && !is_null($optionArray[$key])) {\n        $optionArray[$key] = is_array($optionArray[$key]) ? $optionArray[$key] : [$optionArray[$key]];\n        $optionArray[$key][] = $value;\n    } else {\n        $optionArray[$key] = $value;\n    }\n}\n\n// Accessing the options\nif (isset($optionArray['a'])) {\n    echo "valueForA: {$optionArray['a']}\n";\n}\nif (isset($optionArray['b'])) {\n    echo "valueForB: {$optionArray['b']}\n";\n}\nif (isset($optionArray['c'])) {\n    echo "c is set\n";\n}