<?php\n    // Set up the arguments for WP_Query\n    $args = array(\n        'category_name' => 'teen', // replace with your slug or use category_id\n        'posts_per_page' => 5,     // number of posts to show\n    );\n\n    // Create a new instance of WP_Query\n    $query = new WP_Query($args);\n\n    // Start the loop if there are posts\n    if ($query->have_posts()) {\n        while ($query->have_posts()) {\n            $query->the_post();\n            \n            // Display the title and content or any other details you need\n            echo '<h2>' . get_the_title() . '</h2>';\n            the_content();\n        }\n        \n        // Reset post data to avoid conflicts with other queries\n        wp_reset_postdata();\n    } else {\n        echo 'No posts found';\n    }\n?>