$contents = file_get_contents("Qfile.txt");\n// Normalize line endings to \n\n$contents = str_replace("\r\n", "\n", $contents);\n\nforeach(/* Your loop logic here */) {\n    $pattern = "/Question \"".preg_quote($id, '/')."\"\nAnswer \"\"/";\n\n    // Assuming $string is the answer you want to replace with\n    $replacement = "Question \"$id\"\nAnswer \"". $string . "\"";\n\n    $result = preg_replace($pattern, $replacement, $contents);\n}\n\n// Optionally, write the result back to a file\nfile_put_contents("Qfile1.txt", $result);