<?php\n\nfunction pronounceability_score($word) {\n    $score = 0;\n    $vowels = 'aeiouy';\n    $consonants = 'bcdfghjklmnpqrstvwxyz';\n    \n    // Convert the word to lowercase for uniformity\n    $word = strtolower($word);\n    \n    // Check for consecutive vowels or consonants\n    for ($i = 0; $i < strlen($word) - 1; $i++) {\n        if (strpos($vowels, $word[$i]) !== false && strpos($vowels, $word[$i + 1]) !== false) {\n            // Consecutive vowels penalize slightly\n            $score -= 1;\n        } elseif (strpos($consonants, $word[$i]) !== false && strpos($consonants, $word[$i + 1]) !== false) {\n            // Consecutive consonants penalize more strongly\n            $score -= 2;\n        }\n    }\n    \n    // Check for double letters at the end of a word\n    if (substr($word, -2, 1) == substr($word, -1)) {\n        $score -= 3;\n    }\n    \n    // Longer words get slightly penalized\n    $score -= strlen($word) * 0.5;\n    \n    return -$score; // Negative score means better pronounceability\n}\n\n// Example usage:\n$words = ['nameoic', 'namelet'];\nforeach ($words as $word) {\n    echo "Word: $word, Pronounceability Score: " . pronounceability_score($word) . "\n";\n}\n?>