function storelistingUno(PDO $pdo) {\n    $stmt = $pdo->prepare("INSERT INTO your_table (itemnum, msrp, edprice, itemtype, box, box2, box25, box3, box4, box5, box6, box7, box8, itemcolor, link) VALUES (:itemnum, :msrp, :edprice, :itemtype, :box, :box2, :box25, :box3, :box4, :box5, :box6, :box7, :box8, :itemcolor, :link)");\n    $stmt->execute([\n        ':itemnum' => $_POST['itemnum'],\n        ':msrp' => $_POST['msrp'],\n        ':edprice' => $_POST['edprice'],\n        ':itemtype' => $_POST['itemtype'],\n        ':box' => $_POST['box'],\n        ':box2' => $_POST['box2'],\n        ':box25' => $_POST['box25'],\n        ':box3' => $_POST['box3'],\n        ':box4' => $_POST['box4'],\n        ':box5' => $_POST['box5'],\n        ':box6' => $_POST['box6'],\n        ':box7' => $_POST['box7'],\n        ':box8' => $_POST['box8'],\n        ':itemcolor' => $_POST['itemcolor'],\n        ':link' => $_POST['link']\n    ]);\n    \n    return "yes!";\n}\n\n// Usage\n$pdo = new PDO('mysql:host=your_host;dbname=your_db', 'username', 'password');\n$testValue = storelistingUno($pdo);\necho $testValue;