<?php\n\nrequire 'vendor/autoload.php';\n\nuse PhpOffice\PhpPresentation\IOFactory;\nuse PhpOffice\PhpSpreadsheet\IOFactory as SpreadsheetIOFactory;\n\n// Load the PowerPoint file\n$inputFileName = 'your-presentation.ppt';\n$objPHPPowerPoint = IOFactory::load($inputFileName);\n$sheetIndex = 0;\n$row = 1;\n\n// Create a new Excel document\n$spreadsheet = new \PhpOffice\PhpSpreadsheet\Spreadsheet();\n$worksheet = $spreadsheet->getActiveSheet();\n\nforeach ($objPHPPowerPoint->getAllSlides() as $slide) {\n    foreach ($slide->getShapeCollection() as $shape) {\n        if ($shape instanceof PhpPresentation\Shape\RICHTEXT) {\n            $textRuns = $shape->getText()->getTextRuns();\n            foreach ($textRuns as $textRun) {\n                $bullets = preg_split('/\n+/', trim($textRun->getText()));\n                foreach ($bullets as $bullet) {\n                    if (!empty(trim($bullet))) {\n                        // Write the bullet point to Excel\n                        $worksheet->setCellValueByColumnAndRow(1, $row, htmlspecialchars(trim($bullet)));\n                        // Write the slide index (page number)\n                        $worksheet->setCellValueByColumnAndRow(2, $row, $sheetIndex + 1);\n                        $row++;\n                    }\n                }\n            }\n        }\n    }\n    $sheetIndex++;\n}\n\n// Save the Excel file\n$writer = SpreadsheetIOFactory::createWriter($spreadsheet, 'Xlsx');\n$writer->save('bullet-points.xlsx');\n\necho "Process Completed!";