$frontController = Zend_Controller_Front::getInstance();\n\n// Ensure the root directory used is correct\ndefine('ROOT_DIR', realpath(dirname(__FILE__) . '/../'));\n\n$frontController->setParam('environment', $this->environment)\n                ->setDefaultModule('default') // Use a default module if applicable\n                ->addModuleDirectory(ROOT_DIR . '/modules');\n\n// Setting controller directory if you have one at the root level\n$frontController->setControllerDirectory(ROOT_DIR . '/controllers');