<?php\n\nrequire_once('XMPPHP/XMPP.php');\n\n$host = 'talk.google.com'; // Use appropriate server if not Google Talk\n$port = 5222;\n$user = 'yourusername@gmail.com';\n$pass = 'yourpassword';\n$resource = 'xmpphpbot';\n\n$conn = new XMPPHP_XMPP($host, $port, $user, $pass, $resource);\n\nfunction message_cb($pl){\n    echo "Receveid: ". $pl['body'] . "\n";\n    // Here you can add logic for auto-replying\n}\n\ntry {\n    $conn->connect();\n    $conn->processUntil('session_start');\n    $conn->presence();         // Send initial presence\n    $conn->addHandler('message', 'message_cb'); // Register message handler\n    \n    // Example of sending a message\n    $conn->message('recipient@gmail.com', "Hello, I am an auto-responder!");\n    \n    echo "Chatbot is now running and listening for messages...\n";\n    while (!$conn->disconnected) {\n        $process = @$conn->process(2);\n        if ($process === false) { // Keep Alive ping\n            $conn->send(' ');\n        }\n    }   \n}\n\ncatch(XMPPHP_Exception $e){\n    die($e->getMessage());\n}