<?php\nfunction calculateNumberOfPages($dbConnection, $itemsPerPage) {\n    $query = "SELECT COUNT(*) as total_count FROM your_table_name";\n    $stmt = $dbConnection->prepare($query);\n    $stmt->execute();\n    $result = $stmt->fetch(PDO::FETCH_ASSOC);\n\n    // Total number of items in the table\n    $totalCount = (int)$result['total_count'];\n\n    // Calculate number of pages\n    $numberOfPages = ceil($totalCount / $itemsPerPage);\n\n    return $numberOfPages;\n}\n\n// Example usage:\n$dsn = 'mysql:host=your_host;dbname=your_dbname';\n$username = 'your_username';\n$password = 'your_password';\n\ntry {\n    $dbConnection = new PDO($dsn, $username, $password);\n    $itemsPerPage = 10;\n\n    echo "Number of pages: " . calculateNumberOfPages($dbConnection, $itemsPerPage);\n} catch (PDOException $e) {\n    echo 'Connection failed: ' . $e->getMessage();\n}\n?>