$twitterData = /* your data here */;\n\n// Initialize an associative array to hold the tallies\n$userTextTally = [];\n\n// Iterate over each result\nforeach ($twitterData['results'] as $tweet) {\n    $fromUser = $tweet['from_user'];\n    // Remove the hashtag from the text\n    $cleanedText = str_replace('#runlogger', '', trim($tweet['text']));\n    \n    // Check if this user is already in our tally array\n    if (!isset($userTextTally[$fromUser])) {\n        $userTextTally[$fromUser] = '';\n    }\n    \n    // Concatenate the cleaned text to the user's tally\n    $userTextTally[$fromUser] .= " {$cleanedText}";\n}\n\n// Output the results\nforeach ($userTextTally as $user => $text) {\n    echo "User: $user\n";\n    echo "Text: $text\n\n";\n}