function editArrayValues($array) {\n    // Iterate over each key in the main array\n    foreach ($array as $key => &$subArray) {\n        // Iterate over each value in the sub-array\n        foreach ($subArray as &$value) {\n            // Use a regular expression to find strings that end with "foo" followed by digits\n            if (preg_match('/(foo\d+)$/', $value, $matches)) {\n                // Append "-bar" before the digits\n                $value = str_replace($matches[1], $matches[1] . '-bar', $value);\n            }\n        }\n    }\n    // Return the modified array\n    return $array;\n}\n\n// Example usage\n$array = [\n    "first" => ["baz1"],\n    "second" => ["foo1"],\n    "third" => ["foo2"],\n    "fourth" => ["examplefoo3", "somethingelsefoo4"]\n];\n\n$editedArray = editArrayValues($array);\nprint_r($editedArray);