<?php\n\nfunction createEncryptedZip($sourceFile, $outputFile, $password) {\n    // Check if the file exists\n    if (!file_exists($sourceFile)) {\n        die("Source file does not exist.");\n    }\n\n    // Use exec to call the zip command with AES-256 encryption\n    $command = escapeshellcmd("zip -e -P '$password' '$outputFile' '$sourceFile'");\n    exec($command, $output, $return_var);\n\n    if ($return_var !== 0) {\n        echo "Failed to create encrypted ZIP file.";\n    } else {\n        echo "Encrypted ZIP file created successfully.";\n    }\n}\n\n// Example usage\n$source = 'example.txt';\n$outputZip = 'encrypted_example.zip';\n$password = 'your_secure_password_here';\n\ncreateEncryptedZip($source, $outputZip, $password);\n\n?>