use Drupal\comment\Entity\Comment;\n\nfunction mymodule_print_comment($comment_id) {\n  // Load the comment by its ID.\n  $comment = Comment::load($comment_id);\n\n  if ($comment) {\n    // Access the comment body field (adjust the field name as necessary).\n    $comment_body = $comment->comment_body->value;\n\n    // Print or otherwise use the comment body.\n    print $comment_body;\n  } else {\n    print "Comment not found.";\n  }\n}