<?php\n\n// Array to hold file names\n$files = [];\n\n// Open images folder\nif ($handle = opendir('Images')) {\n    while (false !== ($file = readdir($handle))) {\n        // Skip current and parent directories, index.php, and Thumbnails\n        if ($file != "." && $file != ".." && $file != "index.php" && $file != "Thumbnails") {\n            $files[] = $file;\n        }\n    }\n    closedir($handle);\n}\n\n// Sort files alphabetically\nsort($files);\n\n// Output sorted list\nforeach ($files as $file) {\n    // Strip file extensions and special characters for display purposes\n    $crap = array(".jpg", ".jpeg", ".JPG", ".JPEG", ".png", ".PNG", ".gif", ".GIF", ".bmp", ".BMP", "_", "-");\n    $newstring = str_replace($crap, " ", $file);\n\n    // Write out ul of images and thumbnails from two folders\n    echo "<li><a href=\"Images/$file\" class=\"thickbox\" rel=\"gallery\" title=\"$newstring\"><img src=\"Images/Thumbnails/$file\" alt=\"$newstring\" width=\"300\"></a></li>";\n}\n\n?>