function renderPhpToString($file, array $variables = array()) {\n    // Start output buffering\n    ob_start();\n\n    // Extract variables into local scope\n    extract($variables);\n\n    // Include the file\n    include $file;\n\n    // Get current buffer contents and delete current output buffer\n    return ob_get_clean();\n}\n\n// Example usage\n$text = renderPhpToString('hello.php', array('foo' => 'World'));\necho $text; // Outputs: Hello, World!