<?php\nclass foo {\n\n    public $title;\n\n    // Constructor should not perform any output, just initialize.\n    function __construct() {\n        // Initialization code if necessary\n    }\n\n    /**\n     * I get $title from index.php\n     */\n    public function setTitle($title) {\n        $this->title = $title;\n    }\n\n    // Method to render the HTML\n    public function renderHTML() {\n        echo "<html>\n";\n        echo "<head>\n";\n        echo "<title>" . htmlspecialchars($this->title, ENT_QUOTES, 'UTF-8') . "</title>\n"; // Use htmlspecialchars for security\n        echo "</head>\n";\n        echo "<body>\n";\n\n        // Additional body content can be added here\n\n        echo "</body>\n";\n        echo "</html>\n";\n    }\n\n    function __destruct() {\n        // Cleanup code if necessary, but avoid heavy operations here\n    }\n}\n\n// Usage example:\n$myFoo = new foo();\n$myFoo->setTitle("My Page Title");\n$myFoo->renderHTML(); // Call this method when you want to output the HTML\n?>