class Integer {\n    public $val;\n\n    public function __construct($value) {\n        if (is_int($value)) {\n            $this->val = $value;\n        } else {\n            throw new InvalidArgumentException("Value must be of type integer");\n        }\n    }\n\n    public static function fromInt(int $int): self {\n        return new self($int);\n    }\n}\n\nfunction ExampleFunc(Integer $i) {\n    echo "Value: {$i->val}";\n}\n\nExampleFunc(Integer::fromInt(333));