public function action1Action()\n{\n    if (!$this->loggedIn || !$this->athlete) {\n        $this->_redirect('index');\n    }\n    \n    if (!$this->_request->isXmlHttpRequest()) {\n        // The request was NOT made with JS XmlHttpRequest\n        return;\n    }\n\n    // Disable the layout and the view renderer\n    $this->_helper->layout()->disableLayout();\n    $this->_helper->viewRenderer->setNoRender(true);\n\n    // Do some administration (if needed)\n    \n    $pb = new Zend_Form_Element_Text('PB');\n    $pb->setLabel('PB:')\n       ->addValidator('StringLength', false, array(0, 20))\n       ->setRequired(false);\n    $renderText = $pb->render();\n\n    // Instead of returning the renderText directly,\n    // echo it so that it is sent back as response\n    echo $renderText;\n}