<?php\n\nini_set('default_charset', 'UTF-8');\n\ntry {\n    // Use SOAP_1_2 if needed, or SOAP_1_1 if you are using version 1.1\n    $client = new SoapClient("path/to/your/wsdl", [\n        'encoding' => 'UTF-8',\n        'trace' => true,\n        'exceptions' => true,\n    ]);\n\n    // Call the function with your parameters\n    $response = $client->UpdateApplicationProfile([\n        'suid' => "62eb56ee-45de-4971-9234-54d72bbcd0e4",\n        'appid' => "6be2f269-4ddc-48af-9d47-30b7cf3d0499"\n    ]);\n\n    echo "Response:\n";\n    print_r($response);\n} catch (SoapFault $fault) {\n    // Output the fault message\n    echo 'SOAP Fault: ' . $fault->getMessage();\n    \n    // Output XML response in case of error\n    echo "\nLast Request Headers:\n" . $client->__getLastRequestHeaders() . "\n";\n    echo "\nLast Request:\n" . $client->__getLastRequest() . "\n";\n    echo "\nLast Response Headers:\n" . $client->__getLastResponseHeaders() . "\n";\n    echo "\nLast Response:\n" . $client->__getLastResponse() . "\n";\n}\n?>