class MyClass {\n    public function methodOne($a, $b, $c) {\n        echo "Method One: $a, $b, $c";\n    }\n\n    public function methodTwo($a, $b, $c) {\n        echo "Method Two: $a, $b, $c";\n    }\n\n    public function callMethodDynamically($methodName, $arg1, $arg2, $arg3) {\n        if (method_exists($this, $methodName)) {\n            $this->{$methodName}($arg1, $arg2, $arg3);\n        } else {\n            echo "Method does not exist.";\n        }\n    }\n}\n\n$obj = new MyClass();\n$obj->callMethodDynamically('methodOne', 'Hello', 'World', '!');