function truncateLogFile($filePath, $linesToDelete) {\n    // Check if the file exists\n    if (!file_exists($filePath)) {\n        return "File does not exist.";\n    }\n\n    // Read all lines from the file\n    $lines = file($filePath, FILE_IGNORE_NEW_LINES | FILE_SKIP_EMPTY_LINES);\n\n    // Check if there are enough lines to delete\n    if (count($lines) <= 2 + $linesToDelete) {\n        return "Not enough lines to truncate.";\n    }\n\n    // Keep the first two lines and slice the rest according to linesToDelete\n    $newLines = array_merge(array_slice($lines, 0, 2), array_slice($lines, 2 + $linesToDelete));\n\n    // Write the modified content back to the file\n    if (file_put_contents($filePath, implode("\n", $newLines)) === false) {\n        return "Failed to write to file.";\n    }\n\n    return "File truncated successfully.";\n}\n\n// Example usage\n$filePath = 'path/to/your/logfile.log';\n$linesToDelete = 5; // Number of lines to delete after the first two\n\n$result = truncateLogFile($filePath, $linesToDelete);\necho $result;